import React, { useState, useEffect } from 'react';
import Carousel from 'react-bootstrap/Carousel';
import { Container } from 'react-bootstrap';
import HomeImg from '../assets/images/home.png';

export default function Home() {
  return (
	<Container className="my-4" fluid>
	{/* Top-left welcome message */}
	     <div style={{ position: 'absolute', top: 0, left: 0, zIndex: 10 }}>
	       <h5 className="m-3">¡Bienvenido al administrador de inventario!</h5>
	     </div>
	    <Carousel interval={3000} fade>	
	      <Carousel.Item>
		  <img className="img-fluid d-block mx-auto w-sm-100 w-50" src={HomeImg} alt="Home" />
	        <Carousel.Caption>
	          <h3 className="text-dark">First slide label</h3>
	          <p className='text-dark'>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
	        </Carousel.Caption>
	      </Carousel.Item>
	    </Carousel>
		{/* Bottom-right login prompt */}
		      <div style={{ position: 'absolute', bottom: 0, right: 0, zIndex: 10 }}>
		        <div className="m-3 d-flex flex-column align-items-end">
		          <span>Para empezar por favor inicia sesión</span>
		          <Button variant="primary" className="mt-2">Iniciar sesión</Button>
		        </div>
		      </div>
	</Container>
  );
}
