import React, { useState } from 'react';
import { getCategorias, updateCategoria } from '../services/api';
import { useEffect } from 'react';
import { Card, Col, Container, Row } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { parseDateLong } from '../utils/parseDate';
import Form from 'react-bootstrap/Form';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
import FormCheckInput from 'react-bootstrap/esm/FormCheckInput';
	
export default function Categories({ token }) { 
    const [categories, setCategories] = useState([]);
	const [error, setError] = useState('');
	const [showEditModal, setShowEditModal] = useState(false);
	const [editCategoryId, setEditCategoryId] = useState(null);
	
	useEffect(() => {
		const fetchCategories = async () => {
            try {
                const res = await getCategorias(token);
                setCategories(res.data);
            } catch (err) {
                setError('Error al obtener categorías');
                console.error(err);
            }
        };
        fetchCategories();
	}, []);
	
	const handleEditClick = (categoryId) => {
		setEditCategoryId(categoryId);
        setShowEditModal(true);
    }
	
	const handleFormChange = (e) => {
		const { name, value, type, checked } = e.target;
        setCategories(prevCategories => 
            prevCategories.map(category => 
                category.id === editCategoryId ? {
                    ...category,
                    [name]: type === 'checkbox' ? checked : value
                } : category
            )
        );
    }
	
	const handleSaveChanges = async () => {
		try {
            const categoryToUpdate = categories.find(cat => cat.id === editCategoryId);
            await updateCategoria(editCategoryId, categoryToUpdate, token);
            const res = await getCategorias(token);
            setCategories(res.data);
            setShowEditModal(false);
        } catch (err) {
            setError('Error al guardar cambios');
            console.error(err);
        }
    }
	
	return(
		<Container className="my-4">
		    <Row>
                {error && <div className="alert alert-danger">{error}</div>}
                {categories.map(category => (
                    <Col key={category.id} xs={12} sm={6} md={4} lg={3} className="mb-3">
                        <Card className="shadow-sm">
                            <Card.Body>
							<Card.Title as="h5">{category.name}</Card.Title>
							<Card.Subtitle className="mb-2 text-muted">
								{category.active ? "Categoría Activa" : "Categoría Inactiva"}
							</Card.Subtitle>
							<Button 
							variant="dark btn-sm float-end"
							onClick={() => handleEditClick(category.id)}						                            
							>
							    Editar
							</Button>					
                            </Card.Body>
							<Card.Footer className="text-muted float-end text-end">
							<i>
							    Ultima modificación: {parseDateLong(category.last_update)}
							</i>
							</Card.Footer>
                        </Card>
						<Modal
						    show={editCategoryId === category.id && showEditModal}
							onHide={() => setShowEditModal(false)}
							>
						    <Modal.Header closeButton>
                                <Modal.Title id="edit-category-modal">Editando Categoría: {category.name}</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>
                                <Form>
						            <Row>
	                                    <FloatingLabel
	                                        controlId="floatingInput"
	                                        label="Nombre de la categoría"
	                                        className="mb-3"
	                                    >
	                                        <Form.Control
	                                            type="text"
												name="categoryName"
	                                            placeholder="Nombre de la categoría"
	                                            defaultValue={category.name}
												onChange={handleFormChange}
	                                        />
	                                    </FloatingLabel>
                                    </Row>
                                    <Row>
									<Form.Check
								        type="switch"
								        label="Categoría Activa"
										name="categoryActive"
										className="mx-3"
										defaultChecked={category.active}
										onChange={handleFormChange}
								      />                      
									</Row>
								</Form>
                            </Modal.Body>
                            <Modal.Footer>
                                <Button variant="secondary" onClick={() => setShowEditModal(false)}>
                                    Cerrar
                                </Button>
                                <Button variant="primary" onClick={handleSaveChanges}>
                                    Guardar Cambios
                                </Button>
                            </Modal.Footer>
                        </Modal>

                    </Col>
                ))}
            </Row>
		</Container>
	);
}
