import React, { useState } from 'react';
import { getCategorias } from '../services/api';
import { useEffect } from 'react';
import { Card, Col, Container, Row } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
	
export default function Categories() { 
    const [categories, setCategories] = useState([]);
	const [error, setError] = useState('');
	const [showEditModal, setShowEditModal] = useState(false);
	const [editCategoryId, setEditCategoryId] = useState(null);
	
	useEffect(() => {
		const fetchCategories = async () => {
            try {
                const res = await getCategorias();
                setCategories(res.data);
				console.log('Categorías obtenidas:', res.data);
            } catch (err) {
                setError('Error al obtener categorías');
                console.error(err);
            }
        };
        fetchCategories();
	}, []);
	
	const handleEditClick = (categoryId) => {
		setEditCategoryId(categoryId);
        setShowEditModal(true);
    }
	
	return(
		<Container className="my-4">
		    <Row>
                {error && <div className="alert alert-danger">{error}</div>}
                {categories.map(category => (
                    <Col key={category.id} xs={12} sm={6} md={4} lg={3} className="mb-3">
                        <Card className="shadow-sm">
                            <Card.Body>
							<Card.Title as="h5">{category.name}</Card.Title>
							<Card.Subtitle className="mb-2 text-muted">
								{category.active ? "Categoría Activa" : "Categoría Inactiva"}
							</Card.Subtitle>
							<Button 
							variant="dark btn-sm float-end"
							onClick={() => handleEditClick(category.id)}						                            
							>
							    Editar
							</Button>
                            </Card.Body>
                        </Card>
						<Modal
						    show={editCategoryId === category.id}
							onHide={() => setShowEditModal(false)}
							size="lg"
							aria-labelledby="edit-category-modal"
							centered
							>
						    <Modal.Header closeButton>
                                <Modal.Title id="edit-category-modal">Editar Categoría</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>
                                {/* Aquí iría el formulario para editar la categoría */}
                                <p>Formulario de edición de categoría</p>
                            </Modal.Body>
                            <Modal.Footer>
                                <Button variant="secondary" onClick={() => setShowEditModal(false)}>
                                    Cerrar
                                </Button>
                                <Button variant="primary" onClick={() => setShowEditModal(false)}>
                                    Guardar Cambios
                                </Button>
                            </Modal.Footer>
                        </Modal>

                    </Col>
                ))}
            </Row>
		</Container>
	);
}
