import React, { useState, useEffect } from 'react';
import { getProductos } from '../services/api';
import { Card, Col, Container, Row, Button } from 'react-bootstrap';

function Home() {
	const [productos, setProductos] = useState([]);

	useEffect(() => {
		const fetchProductos = async () => {
			try {
				const res = await getProductos();
				setProductos(res.data); // Asegúrate de acceder a .data
			} catch (err) {
				console.error('Error al obtener productos:', err);
			}
		};

		fetchProductos();
	}, []);

	const crearProducto = productos.map(producto => (
		<Col key={producto.id} className="mb-3">
			<Card>
				<Card.Body>
					<Card.Title>{producto.name}</Card.Title>
					<Card.Text>{producto.price}</Card.Text>
					<div xs="auto">
						<Button size="sm" variant="primary" className="mx-1">Editar</Button>
						<Button size="sm" variant="danger" className="mx-1">Eliminar</Button>
					</div>
				</Card.Body>
			</Card>
		</Col>
	));

	return (
		<Container className="my-3">
			<Row>
				{crearProducto}
			</Row>
		</Container>
	);
}

export default Home;
