import React, { useState, useEffect } from 'react';
import { getProductos } from '../services/api';
import { Card, Col, Container, Row, Button, Modal } from 'react-bootstrap';

function Home() {
	const [productos, setProductos] = useState([]);
	const [show, setShow] = useState(false);
	
	const handleClose = () => setShow(false);
	const handleShow = () => setShow(true);

	useEffect(() => {
		const fetchProductos = async () => {
			try {
				const res = await getProductos();
				setProductos(res.data); // Asegúrate de acceder a .data
			} catch (err) {
				console.error('Error al obtener productos:', err);
			}
		};

		fetchProductos();
	}, []);

	return (
		<Container className="my-3">
			<Row>
				{productos.map(producto => (
					<Col key={producto.id} className="mb-3">
						<Card>
							<Card.Body>
								<Card.Title>{producto.name}</Card.Title>
								<Card.Text>{producto.price}</Card.Text>
								<div xs="auto">
									<Button onClick={handleShow} size="sm" variant="secondary" className="mx-1">Editar</Button>
									<Button size="sm" variant="dark" className="mx-1">Eliminar</Button>
								</div>
							</Card.Body>
						</Card>
					</Col>
                ))}
			</Row>
		</Container>
	);
}

export default Home;
