import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:8081', // cambia según tu puerto/API
  headers: {
    'Content-Type': 'application/json',
  },
});
//request login
export const login = (credentials) => api.post('/login', credentials);
export const getUsuarios = (token) =>
  api.get('/usuarios', { headers: { Authorization: `Bearer ${token}` } });
export const createUsuario = (formData) =>
	    api.post('/usuarios', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
//request productos
export const getProductos = () => 
  api.get('/productos');
export const updateProducto = (id, formData) => 
  api.put(`/productos/${id}`, formData);
export const deleteProducto = (id) =>
	api.delete(`/productos/${id}`);
export const getProductoCategorias = (id) => 
  api.get(`/${id}/categorias`);
//request categorias
export const getCategorias = () => 
  api.get('/categorias');
export const createCategoria = (formData) =>
	api.post('/categorias', formData);
export const updateCategoria = (id, formData) =>
	api.put(`/categorias/${id}`, formData);
export const deleteCategoria = (id) =>
	api.delete(`/categorias/${id}`);
export default api;
