import React, { useState, useEffect } from 'react';
import Carousel from 'react-bootstrap/Carousel';
import { Container } from 'react-bootstrap';
import HomeImg from '../assets/images/home.png';
import Button from 'react-bootstrap/Button';

export default function Home() {
  return (
	<Container className="my-4" fluid>
	{/* Top-left welcome message */}
	     <div style={{ position: 'absolute', top: 100, left: 50, zIndex: 10 }}>
	       <h5 className="m-3">¡Bienvenido al administrador de inventario!</h5>
		    <p className='text-mute'>Para empezar, por favor inicia sesión.</p>		 
	     </div>
	    <Carousel interval={3000} fade>	
	      <Carousel.Item>
		  <img className="img-fluid d-block mx-auto w-sm-100 w-50" src={HomeImg} alt="Home" />
	      </Carousel.Item>
	    </Carousel>
		{/* Bottom-right login prompt */}
		      <div style={{ position: 'absolute', bottom: 50, right: 50, zIndex: 10 }}>
		        <div className="m-3 d-flex flex-column align-items-end">
		          <span>Para empezar por favor inicia sesión</span>
		          <Button variant="success" className="mt-2"></Button>
		        </div>
		      </div>
	</Container>
  );
}
