import React, { useEffect, useState } from 'react';
import { getUsuarios } from '../services/api';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Card from 'react-bootstrap/Card';

function Users({ token }) {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!token) return;
    getUsuarios(token)
      .then(res => setUsers(res.data))
      .catch(() => setError('Unauthorized or failed to fetch users'));
  }, [token]);

  if (!token) return null;
  if (error) return <div>{error}</div>;

  return (
	<Row>
	{users.map(user => 
		<Col key={user.id}>
				<Card  className="shadow-sm m-3">
				    <Card.Header>Usuario {user.id}</Card.Header>
			        <Card.Body>
					    <ul className="list-group list-group-flush">
					        <li className="list-group-item">Nombre: {user.name}</li>
			                <li className="list-group-item">Email: {user.email}</li>
			                <li className="list-group-item">Rol: {user.role}</li>
			                <li className="list-group-item">Activo: {user.active ? 'Sí' : 'No'}</li>
					    </ul>
					</Card.Body>
			</Card>
		</Col>)}
	</Row>
  );
}

export default Users;
