import React, { useState, useEffect } from 'react';
import { getProductos, getProductoCategorias, getCategorias, 
		updateProducto, deleteProducto } from '../services/api';
import { Card, Col, Container, 
		Row, Button, Modal, 
		Form, InputGroup, FloatingLabel } from 'react-bootstrap';

function Home() {
	const [products, setProducts] = useState([]);
	const [editProductId, setEditProductId] = useState(null);
	const [deleteId, setDeleteId] = useState(null);
	const [editForm, setEditForm] = useState({ name: '', price: '', active: false });

	// Para manejar categorías
	//const [category, setCategory] = useState(null);
	//const [productCategory, setProductCategory] = useState(null);
	
	useEffect(() => {
		const fetchproducts = async () => {
			try {
				const res = await getProductos();
				setProducts(res.data); // Asegúrate de acceder a .data
			} catch (err) {
				console.error('Error al obtener products:', err);
			}
		};
		fetchproducts();
	}, []);

	// Open modal and initialize form
	  const handleEditClick = (producto) => {
	    setEditProductId(producto.id);
	    setEditForm({ name: producto.name, price: producto.price, active: producto.active });
	  };

	  // Handle form changes
	  const handleFormChange = (e) => {
	    const { name, value, type, checked } = e.target;
	    setEditForm(prev => ({
	      ...prev,
	      [name]: type === 'checkbox' ? checked : value
	    }));
	  };

	  // Save changes
	  const handleSaveChanges = async () => {
	    await updateProducto(editProductId, editForm);
	    const res = await getProductos();
	    setProducts(res.data);
	    setEditProductId(null);
	  };
	  
	  // Handle delete confirmation
	  const handleDelete = async (id) => {
        await deleteProducto(id);
        //setProducts(products.filter(producto => producto.id !== id));
        setDeleteId(null);
      };

	  return (
	    <Container className="my-3">
	      <Row>
	        {products.map(producto => (
	          <Col key={producto.id} className="mb-3">
	            <Card className='shadow-sm'>
	              <Card.Body>
	                <Card.Title>{producto.name}</Card.Title>
	                <Card.Text>{producto.price}</Card.Text>
	                <div xs="auto">
	                  <Button
	                    onClick={() => handleEditClick(producto)}
	                    size="sm"
	                    variant="secondary"
	                    className="mx-1"
	                  >
	                    Editar
	                  </Button>
	                  <Button
	                    size="sm"
	                    variant="dark"
	                    className="mx-1"
						onClick={deleteId === producto.id ? 
							() => setDeleteId(null) 
							: () => setDeleteId(producto.id)
						}
	                  >
	                    Eliminar
	                  </Button>
	                </div>
	              </Card.Body>
	            </Card>
				{/* Delete Confirmation Modal */}
				<Modal
                  show={deleteId === producto.id}
                  onHide={() => setDeleteId(null)}
				  >
				 <Modal.Header closeButton>Eliminando {producto.name}</Modal.Header>
				 <Modal.Body>
  	                ¿Estás seguro de que quieres eliminar este producto?
				 </Modal.Body>
				 <Modal.Footer>
 	                <Button variant="secondary" onClick={() => setDeleteId(null)}>
 	                  Close
 	                </Button>
 	                <Button 
					variant="danger"
					onClick={() => handleDelete(producto.id)}
					>
 	                  Delete
 	                </Button>
 	              </Modal.Footer>
				 </Modal>

	            {/* Edit Modal */}
	            <Modal
	              show={editProductId === producto.id}
	              onHide={() => setEditProductId(null)}
	            >
	              <Modal.Header closeButton>
	                <Modal.Title>Editando {producto.name}</Modal.Title>
	              </Modal.Header>
	              <Modal.Body>
	                <Form>
	                  <Row>
	                    <FloatingLabel
	                      controlId="floatingInput"
	                      label="Nombre del producto"
	                      className="mb-3"
	                    >
	                      <Form.Control
	                        type="text"
	                        name="name"
	                        value={editForm.name}
	                        onChange={handleFormChange}
	                        placeholder="Nombre del producto"
	                      />
	                    </FloatingLabel>
	                  </Row>
	                  <Row>
	                    <Form.Group as={Col}>
	                      <Form.Label>Precio</Form.Label>
	                      <InputGroup>
	                        <InputGroup.Text>$</InputGroup.Text>
	                        <Form.Control
	                          type="number"
	                          name="price"
	                          value={editForm.price}
	                          onChange={handleFormChange}
	                          placeholder="Precio del producto"
	                        />
	                      </InputGroup>
	                    </Form.Group>
	                  </Row>
	                  <Row className="m-2">
	                    <Form.Check
	                      type="switch"
	                      name="active"
	                      checked={editForm.active}
	                      onChange={handleFormChange}
	                      label="Producto activo"
	                    />
	                  </Row>
	                </Form>
	              </Modal.Body>
	              <Modal.Footer>
	                <Button variant="secondary" onClick={() => setEditProductId(null)}>
	                  Close
	                </Button>
	                <Button variant="primary" onClick={handleSaveChanges}>
	                  Save Changes
	                </Button>
	              </Modal.Footer>
	            </Modal>
	          </Col>
	        ))}
	      </Row>
	    </Container>
	  );
}

export default Home;
