import React, { useEffect, useState } from 'react';
import { getUsuarios } from '../services/api';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';


function Users({ token, activeUser }) {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!token) return;
    getUsuarios(token)
      .then(res => setUsers(res.data))
      .catch(() => setError('Unauthorized or failed to fetch users'));
  }, [token]);

  if (!token) return null;
  if (error) return <div>{error}</div>;
  
  const filteredUsers = users.filter(user => user.username !== activeUser);

  return (
	<Container className="my-4">
	<Row>
	{filteredUsers.map(user => 
		<Col key={user.id} xs={12} sm={6} md={4} lg={3} className="mb-3">
			<Card className="shadow-sm">
			    <Card.Header>{user.name}</Card.Header>
		        <Card.Body>
				    <ul className="list-group list-group-flush">
		                <li className="list-group-item">Username: {user.username}</li>
		                <li className="list-group-item">Rol: {user.role}</li>
		                <li className="list-group-item">Activo: {user.active ? 'Sí' : 'No'}</li>
				    </ul>
				</Card.Body>
			</Card>
		</Col>)}	
	</Row>
	</Container>
  );
}

export default Users;
