import React, { useState, useEffect } from 'react';
import { getProductos } from '../services/api';
import { Card, Col, Container, Row, Button, Modal } from 'react-bootstrap';

function Home() {
	const [productos, setProductos] = useState([]);
	const [show, setShow] = useState(false);
	const [editId, setEditId] = useState(null);
	const [deleteId, setDeleteId] = useState(null);
	
	const handleClose = () => setShow(false);
	const handleShow = () => setShow(true);

	useEffect(() => {
		const fetchProductos = async () => {
			try {
				const res = await getProductos();
				setProductos(res.data); // Asegúrate de acceder a .data
			} catch (err) {
				console.error('Error al obtener productos:', err);
			}
		};

		fetchProductos();
	}, []);

	return (
		<Container className="my-3">
			<Row>
			{productos.map(producto => (
			  <Col key={producto.id} className="mb-3">
			    <Card>
			      <Card.Body>
			        <Card.Title>{producto.name}</Card.Title>
			        <Card.Text>{producto.price}</Card.Text>
			        <div xs="auto">
			          <Button
			            onClick={() => setEditId(producto.id)}
			            size="sm"
			            variant="secondary"
			            className="mx-1"
			          >
			            Editar
			          </Button>
			          <Button
			            onClick={() => setDeleteId(producto.id)}
			            size="sm"
			            variant="dark"
			            className="mx-1"
			          >
			            Eliminar
			          </Button>
			        </div>
			      </Card.Body>
			    </Card>

			    {/* Edit Modal */}
			    <Modal
			      show={editId === producto.id}
			      onHide={() => setEditId(null)}
			    >
			      <Modal.Header closeButton>
			        <Modal.Title>Editando {producto.name}</Modal.Title>
			      </Modal.Header>
			      <Modal.Body>Edit form for {producto.name} here.</Modal.Body>
			      <Modal.Footer>
			        <Button variant="secondary" onClick={() => setEditId(null)}>
			          Close
			        </Button>
			        <Button variant="primary" onClick={() => setEditId(null)}>
			          Save Changes
			        </Button>
			      </Modal.Footer>
			    </Modal>

			    {/* Delete Modal */}
			    <Modal
			      show={deleteId === producto.id}
			      onHide={() => setDeleteId(null)}
			    >
			      <Modal.Header closeButton>
			        <Modal.Title>Eliminar {producto.name}</Modal.Title>
			      </Modal.Header>
			      <Modal.Body>
			        Are you sure you want to delete {producto.name}?
			      </Modal.Body>
			      <Modal.Footer>
			        <Button variant="secondary" onClick={() => setDeleteId(null)}>
			          Cancel
			        </Button>
			        <Button variant="danger" onClick={() => setDeleteId(null)}>
			          Delete
			        </Button>
			      </Modal.Footer>
			    </Modal>
			  </Col>
			))}
			</Row>
		</Container>
	);
}

export default Home;
