import React, { useEffect, useState } from 'react';
import { getUsuarios } from '../services/api';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Card from 'react-bootstrap/Card';
import CardGroup from 'react-bootstrap/CardGroup';

function Users({ token }) {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!token) return;
    getUsuarios(token)
      .then(res => setUsers(res.data))
      .catch(() => setError('Unauthorized or failed to fetch users'));
  }, [token]);

  if (!token) return null;
  if (error) return <div>{error}</div>;

  return (
	<Row>
	<Col xs={10} className="mx-auto">
	<CardGroup className="my-3">
	{users.map(user => 
				<Card key={user.id} className="shadow-sm">
				    <Card.Header>{user.name}</Card.Header>
			        <Card.Body>
					    <ul className="list-group list-group-flush">
					        <li className="list-group-item">Nombre: {user.name}</li>
			                <li className="list-group-item">Email: {user.email}</li>
			                <li className="list-group-item">Rol: {user.role}</li>
			                <li className="list-group-item">Activo: {user.active ? 'Sí' : 'No'}</li>
					    </ul>
					</Card.Body>
			</Card>)}
	</CardGroup>
	</Col>
	</Row>
  );
}

export default Users;
