import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import MyNavbar from './components/navbar';
import Home from './components/home';
import Users from './components/users';
import Productos from './components/products';
import Categorias from './components/categories';
import { parseJwt } from './utils/parseJWT';
import AppBreadcrumb from './components/breadcrumb';
import {  getCategorias, getRoles } from './services/api';


function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [activeUser, setActiveUser] = useState(null);
  const [roles, setRoles] = useState([]);
  const [allCategories, setAllCategories] = useState([]);
  const [allRoles, setAllRoles] = useState([]);
  
  useEffect(() => {
	if (!token) {
      setActiveUser(null);
	  setAllCategories([]);
      setRoles([]);
	  return;
	}
    const fetchCategoriesAndRoles = async () => {
        try {
		    const categoriesResponse = await getCategorias(token);
		    setAllCategories(categoriesResponse.data);

		    const rolesResponse = await getRoles(token);
		    setAllRoles(rolesResponse.data);
		} catch (error) {
		    console.error('Error fetching categories or roles:', error);
		}
		}
	    fetchCategoriesAndRoles();
	}, [token]);
					
  
  
  useEffect(() => {
    if (token) {
      const payload = parseJwt(token);
	  console.log('Parsed JWT payload:', payload);
      setActiveUser(payload?.sub); 
	  setRoles(payload?.roles || []);
    } else {
      setActiveUser(null);
    }
  }, [token]);

  return (
	  <BrowserRouter>
        <MyNavbar 
          token={token} 
          setToken={setToken} 
          activeUser={activeUser} 
          setActiveUser={setActiveUser}
		  roles={roles} setRoles={setRoles}
        />
		{token && <AppBreadcrumb activeUser={activeUser} />}
	    <Routes>
		  <Route path="/" element={<Home token={token} />} />
		  <Route  path={"productos"} element={<Productos token={token} />} />
		  <Route element={<Categorias token={token} />}  path={"categorias"} />
		  {token && <Route path="/usuarios" element={<Users 
													token={token} activeUser={activeUser} 
													roles={roles} />} />}

		 </Routes>
	  </BrowserRouter>
  );
}

export default App;
