import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import MyNavbar from './components/navbar';
import Users from './components/users';
import Productos from './components/products';
import Categorias from './components/categories';
import { parseJwt } from './utils/parseJWT';


function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [activeUser, setActiveUser] = useState(null);
  
  useEffect(() => {
    if (token) {
      const payload = parseJwt(token);
	  console.log('Parsed JWT payload:', payload);
      setActiveUser(payload?.sub); // or payload.id if your JWT has user id
    } else {
      setActiveUser(null);
    }
  }, [token]);

  return (
	  <BrowserRouter>
        <MyNavbar 
          token={token} 
          setToken={setToken} 
          activeUser={activeUser} 
          setActiveUser={setActiveUser} 
        />
	    <Routes>
		  <Route path="/" element={<h1>Bienvenido a la Aplicaci√≥n de Inventario</h1>} />
		  <Route  path={"productos"} element={<Productos />} />
		  <Route element={<Categorias />}  path={"categorias"} />
		  {token && <Route path="/usuarios" element={<Users token={token} activeUser={activeUser} />} />}

		 </Routes>
	  </BrowserRouter>
  );
}

export default App;
