import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, useLocation, Link } from "react-router-dom";
import MyNavbar from './components/navbar';
import Home from './components/home';
import Users from './components/users';
import Productos from './components/products';
import Categorias from './components/categories';
import { parseJwt } from './utils/parseJWT';
import Breadcrumb from 'react-bootstrap/Breadcrumb';


function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [activeUser, setActiveUser] = useState(null);
  const [roles, setRoles] = useState([]);
  
  const location = useLocation();
   const pathnames = location.pathname.split('/').filter(x => x);
  
  useEffect(() => {
    if (token) {
      const payload = parseJwt(token);
	  console.log('Parsed JWT payload:', payload);
      setActiveUser(payload?.sub); 
	  setRoles(payload?.roles || []);
    } else {
      setActiveUser(null);
    }
  }, [token]);

  return (
	  <BrowserRouter>
        <MyNavbar 
          token={token} 
          setToken={setToken} 
          activeUser={activeUser} 
          setActiveUser={setActiveUser}
		  roles={roles} setRoles={setRoles}
        />
		<Breadcrumb>
		      <Breadcrumb.Item linkAs={Link} linkProps={{ to: '/' }}>
		        Home
		      </Breadcrumb.Item>
		      {pathnames.map((value, idx) => {
		        const to = `/${pathnames.slice(0, idx + 1).join('/')}`;
		        const isLast = idx === pathnames.length - 1;
		        return isLast ? (
		          <Breadcrumb.Item active key={to}>{value}</Breadcrumb.Item>
		        ) : (
		          <Breadcrumb.Item linkAs={Link} linkProps={{ to }} key={to}>
		            {value}
		          </Breadcrumb.Item>
		        );
		      })}
		    </Breadcrumb>
	    <Routes>
		  <Route path="/" element={<Home token={token} />} />
		  <Route  path={"productos"} element={<Productos token={token} />} />
		  <Route element={<Categorias token={token} />}  path={"categorias"} />
		  {token && <Route path="/usuarios" element={<Users 
													token={token} activeUser={activeUser} 
													roles={roles} />} />}

		 </Routes>
	  </BrowserRouter>
  );
}

export default App;
